INSERT INTO public.certification VALUES ('3bf6cafe-07e2-4781-9e4e-51ed777130bc', '37054', '2025-07-24 15:32:24.106+00', '2025-08-29 13:44:30.420481+00', 'CQP Mcanicien en machines tournantes sous pression', NULL, NULL, NULL, NULL, 4, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2025-11-23 00:00:00+00', 'CQP Mcanicien en machines tournantes sous pression', 4, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Le mcanicien en machines tournantes sous pression effectue des oprations de maintenance prventive ou corrective, ou dinstallation ou damlioration de ces machines telles que des pompes (centrifuges, volumtriques rotatives et  alternatives), des compresseurs (centrifuges, alternatifs), des turbines ( gaz,  vapeur), et leurs lments  mcaniques associs, sur un site industriel et en atelier de maintenance, utilises dans des industries de la mtallurgie, de lnergie (raffineries, sidrurgies, centrales lectriques, plateformes gazires et ptrolires), de la chimie et  ptrochimie, de lagroalimentaire). Elles sont dites  sous pression , car elles transmettent la force centrifuge  un fluide (eau, sauce, hydrocarbure, acide, chaux ) ou  un gaz (vapeur, oxygne) qui entre en basse pression et qui en sort en haute pression. Elles sont soit entrainantes ou soit entraines, motrices (moteurs, turbines) ou gnratrices (alternateurs, pompes, compresseurs), et ont toutes un rotor (pice en mouvement) qui tourne sur un stator (pice fixe). Elles sont de toutes  dimensions et poids, de quelques centimtres  plusieurs dizaines de mtre, de quelques grammes  plusieurs  centaines de tonnes. Elles doivent tre capables de rsister  des conditions extrmes, et des vibrations (par exemple  une turbine  vapeur fonctionne  560 degrs et 160 bar).', '3bf6cafe-07e2-4781-9e4e-51ed777130bc');
